import React from 'react';
import { CartReducer } from './CartReducer';

export const CartContext = React.createContext(Array ());

export function CartContextProvider({children}: any) {
  let storage: any = [];
  let getData = localStorage.getItem("cart");
  storage = getData ? JSON.parse(getData) : [];

  const initialState = { cartItems: storage, checkout: false };

  const [todos, dispatch] = React.useReducer(
    CartReducer,
    initialState
  );

  const increase = (payload: any) => {
    dispatch({type: payload, payload})
  } 

  return ( 
    <CartContext.Provider value={todos} >
        { children }
    </CartContext.Provider>
 );
};
