import { useEffect } from "react";

export function List(props: any) {

  function renderDatas() {
    return props.datas.map((data: any, key: any) => {
      return(
        <div key={key} className="col-3 mb-4 d-flex">
          <div className="card">
            <img src={data.image} className="card-img-top" alt="..." />
            <div className="card-body d-flex flex-column justify-content-between">
              <h5 className="card-title">{data.name}</h5>
              <p className="card-text">{data.description}</p>
              <button className="btn btn-primary" onClick={() => addCart(data)}>Add To Cart</button>
            </div>
          </div>
        </div>
      );
    });
  }

  function addCart(data: object) {
    let dataCarts: any;
    dataCarts = JSON.parse(localStorage.getItem("cart") || '') || [];
    dataCarts.push(data);
    localStorage.setItem('cart', JSON.stringify(dataCarts));
  }

  return (
    <div className="container">
      <div className="row">
        {renderDatas()}
      </div>
    </div>
  );
};
