import { MinusCircleIcon, PlusCircleIcon, TrashIcon } from "../../components/icons";
import { useCart } from "../../hooks/useCart";

export function CartItem() {
    const {cartItems, increase, decrease, removeItem, itemCount, total, clearItem}: any = useCart();

  function renderDatas() {
    return cartItems.map((data: any, key: any) => {
      return(
        <div className="row no-gutters py-2">
            <div className="col-sm-2 p-2">
                <img
                alt={data.name}
                style={{margin: "0 auto", maxHeight: "50px"}} 
                src={data.image} className="img-fluid d-block"/>
            </div>
            <div className="col-sm-4 p-2">
                <h5 className="mb-1">{data.name}</h5>
                <p className="mb-1">Price: {data.price} </p>

            </div>
            <div className="col-sm-2 p-2 text-center ">
                 <p className="mb-0">Qty: {data.quantity}</p>
            </div>
            <div className="col-sm-4 p-2 text-right">
                 <button 
                 onClick={() => increase(data)}
                 className="btn btn-primary btn-sm mr-2 mb-1">
                     <PlusCircleIcon width={"20px"}/>
                 </button>

                 {
                     data.quantity > 1 &&
                     <button
                    onClick={() => decrease(data)}
                    className="btn btn-danger btn-sm mb-1">
                        <MinusCircleIcon width={"20px"}/>
                    </button>
                 }

                {
                     data.quantity === 1 &&
                     <button
                    onClick={() => removeItem(data)}
                    className="btn btn-danger btn-sm mb-1">
                        <TrashIcon width={"20px"}/>
                    </button>
                 }

            </div>
        </div>
      );
    });
  }

  return (
    <>
        {
          cartItems.length > 0 && 
          <div className="row">
            <div className="col-9">
            {
                    renderDatas()
                  }
            </div>

                <div className="col-3">
                  <div className="card card-body">
                      <p className="mb-1">Total Items</p>
                      <h4 className=" mb-3 txt-right">{itemCount}</h4>
                      <p className="mb-1">Total Payment</p>
                      <h3 className="m-0 txt-right">{total}</h3>
                      <hr className="my-4"/>
                      <div className="text-center">
                          <button type="button" className="btn btn-primary mb-2">CHECKOUT</button>
                          <button onClick={() => clearItem()} type="button" className="btn btn-outlineprimary btn-sm">CLEAR</button>
                      </div>
                  </div>
                </div>
          </div>
        }
    </>
  );
}
