import firebase from 'firebase';
import React, { useEffect, useState } from 'react';

export const ProductsContext = React.createContext(Array ());

export function ProductsContextProvider({children}: any) {
  const [loading, setLoading] = useState(true);
  const [datas, setDatas] = useState([]);

  async function getData() {
    let items : any = [];
    await firebase.firestore().collection("hai_shops").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        items.push({ 
          name: doc.data().name,
          image: doc.data().image,
          description: doc.data().description
        });
      });
      setDatas(items);
    });
  }

  useEffect( () => {
    getData().then(() => {
      setLoading(false);
    });
  }, []);

  const dataProduct = [
    loading,
    datas
  ]

  return ( 
    <ProductsContext.Provider value={dataProduct} >
        { children }
    </ProductsContext.Provider>
 );
};
