import { Header } from "./shared";
import { Helmet } from 'react-helmet-async';

const Layout = ({title, description, children}: any) => {
  return (
    <>
      <Header />
      <Helmet>
        <title>{title ? title + "Shop Cart" : "Shop Cart"}</title>
        <meta name = "description" content={ description || "Shop Cart" } />
      </Helmet>
      <main>
        {children}
      </main>
    </>
  );
}

export default Layout;
