import { Header } from "./shared";
import { Helmet } from 'react-helmet-async';

export default function Layout(title: string, description: string, children: any) {
  return <>
    <Header />
    <Helmet>
      <title>{title ? title + "Shop Cart" : "Shop Cart"}</title>
      <meta name = "description" content={ description || "Shop Cart" } />
    </Helmet>
    <main>
      {children}
    </main>
  </>;
}
