import { useContext, useState } from "react";
import { ProductsContext } from "../../contexts/ProductsContext";

export function List() {  
  let dataCarts: any = [];
  let getData = localStorage.getItem("cart");
  dataCarts = getData ? JSON.parse(getData) : [];

  const [load, setLoad] = useState([]);
  const datas: any = useContext(ProductsContext);

  function isInCart(data: any) {
    return !!dataCarts.find((item: any) => item.name === data.name);
  }

  function NewlineText(props: any) {
    const text = props.text;
    return text.split('\n').map((str: any) => <p>{str}</p>);
  }

  function renderDatas() {
    return datas.map((data: any, key: any) => {
      return(
        <div key={key} className="col-3 mb-4 d-flex">
          <div className="card">
            <img src={data.image} className="card-img-top" alt="..." />
            <div className="card-body d-flex flex-column justify-content-between">
              <h5 className="card-title">{data.name}</h5>
              <NewlineText text={data.name} />
              <p className="card-text">{data.description}</p>
              {
                  isInCart(data) && 
                  <button 
                  onClick={() => increase(data.name)}
                  className="btn btn-outline-primary btn-sm">Add more</button>
              }

              {
                  !isInCart(data) && 
                  <button 
                  onClick={() => addCart(data)}
                  className="btn btn-primary btn-sm">Add to cart</button>
              }
            </div>
          </div>
        </div>
      );
    });
  }

  function addCart(data: object) {
    dataCarts.push({...data, quantity: 1});
    localStorage.setItem('cart', JSON.stringify(dataCarts));
    setLoad([]);
  }

  function increase(name: string) {
    dataCarts.find((item: any) => item.name === name).quantity++;
    localStorage.setItem('cart', JSON.stringify(dataCarts));
  }

  return (
    <div className="container">
      <div className="row">
        {renderDatas()}
      </div>
    </div>
  );
};
