import firebase from "firebase";
import { useEffect, useState } from "react";
import { Header } from "../Header";
import { List } from "./List";

interface Datas {
  name: string,
  image: string,
  description: string,
}

export function Home() {
  const [datas, setDatas] = useState(Array);

  async function test() {
    let items : any = [];
    await firebase.firestore().collection("hai_shops").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        items.push({ 
          name: doc.data().name,
          image: doc.data().image
        });
      });
      setDatas(items);
    });
  }

  useEffect( () => {
    test();
  }, [])

  return (
    <div>
        <Header />
        <List datas={datas}/>
    </div>
  );
};
