import firebase from "firebase";

export function getData() {
    let items : any = [];
    firebase.firestore().collection("hai_shops").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        items.push({ 
          name: doc.data().name,
          image: doc.data().image,
          description: doc.data().description
        });
      });
    });

    return items;
  }
