import firebase from "firebase";


async function data() {
    let items : any = [];
    await firebase.firestore().collection("hai_shops").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        items.push({ 
          name: doc.data().name,
          image: doc.data().image,
          description: doc.data().description
        });
      });
    });

    return items;
 }

 export const getData: any = data();
