import { useEffect, useState } from "react";

export function List(props: any) {


  const [loading, setLoading] = useState(true);

  async function renderDatas() {
    let a = await props.datas.map((data: any, key: any) => {
      return(
        <div key={key} className="col-3 mb-4 d-flex">
          <div className="card">
            <img src={data.image} className="card-img-top" alt="..." />
            <div className="card-body d-flex flex-column justify-content-between">
              <h5 className="card-title">{data.name}</h5>
              <p className="card-text">{data.description}</p>
              <a href="#" className="btn btn-primary">Add To Cart</a>
            </div>
          </div>
        </div>
      );
    });
  }

  useEffect( () => {
    renderDatas().then(() =>{
      setLoading(false);
    });
  }, [])

  return (
    <>
    { loading
        ?
          <div style={{width: "100%", height: "100%", position: "fixed", top: "0", left: "0", right: "0", bottom: "0" }} className="d-flex justify-content-center align-items-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        :
        <div className="container">
        <div className="row">
          {renderDatas()}
        </div>
      </div>
        }
    </>

  );
};
