import firebase from "firebase";


let items : any = [];

async function getDataa() {
    await firebase.firestore().collection("hai_shops").get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        items.push({ 
          name: doc.data().name,
          image: doc.data().image,
          description: doc.data().description
        });
      });
    });
}

 export const getData: any = items;
